mapper.update:
UPDATE installment_savings 
SET deposit_type = #{deposit_type}, 
    interest_rate = #{interest_rate}
WHERE deposit_type = #{deposit_type}

mapper.select:
SELECT COUNT(*)
FROM installment_savings
WHERE savings_account_num = #{accountNumber}

mapper.update:
UPDATE account 
SET account_MONEY = account_MONEY - #{amount} 
WHERE account_id = #{fromAccountNum};
UPDATE account 
SET account_MONEY = account_MONEY + #{amount} 
WHERE account_id = #{toAccountNum};

mapper.select:
SELECT COUNT(*)
FROM installment_savings
WHERE savings_account_num = #{savingsAccountNum}
  AND savings_account_password = #{password}

SELECT COUNT(*)
FROM installment_savings
WHERE savings_account_num = #{accountNumber}
